import base64
import io
from PIL import Image

image_base64 = "data:image/png;base64,...""" # Full content

try:
    image_bytes = base64.b64decode(image_base64.split(",", 1)[1]) # Split the data URL and decode only the base64 part
    img = Image.open(io.BytesIO(image_bytes))
    img.load()
    print("Image successfully loaded and identified by Pillow.")
except Exception as e:
    print(f"Error loading image with Pillow: {e}")